import java.lang.*;
public class LargestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5, j=6, i=4;
		if (x>j&&x>i)
			System.out.println("x is greatest");
		else if (j>x&&j>i)
			System.out.println("j is greatest");
		else 
			System.out.println("z is greatest");	
	}

}
